mapper.insert:
		DECLARE
		    V_CNT NUMBER;
		BEGIN
    		SELECT COUNT(*)
    		INTO V_CNT FROM(
    		SELECT * FROM BOM
    		WHERE GICODE=#{gicode} AND MICODE = #{micode});

    		IF V_CNT > 0 THEN
    		UPDATE BOM
    		SET BAM = #{bam}, BNOTE = #{bnote}
    		WHERE GICODE = #{gicode} and micode = #{micode};
    		ELSE
    		INSERT INTO BOM
    		VALUES(BOM_BCODE_SEQ.NEXTVAL, #{gicode}, #{micode}, #{bam}, #{bnote});
    		END IF;
		END;
