mapper.select:
SELECT m.cnnum, c.csname, g.giname, m.cnam, m.cnrest, m.cndate, m.cnperiod, m.cnstatus, g.giunit FROM contract m, costomer c, goodsinfo g WHERE (c.cscode = m.cscode AND g.gicode = m.gicode)
AND (m.cndate BETWEEN to_date(#{startDt}, 'yyyy-MM-dd') AND to_date(#{endDt}, 'yyyy-MM-dd'))
AND m.cscode = #{cusCode}
AND m.gicode =#{goodsCode}
AND m.cnnum = #{cnCode}

SELECT p.prnum, p.cnnum, g.giname, r.csname, c.cndate, c.cnperiod, p.prclose, p.prstatus, c.cnam FROM contract c, prodreq p, costomer r, goodsinfo g WHERE (c.cnnum = p.cnnum AND c.gicode = g.gicode AND c.cscode = r.cscode)
AND (c.cndate BETWEEN to_date(#{startCd}, 'yyyy-MM-dd') AND to_date(#{endCd}, 'yyyy-MM-dd'))
AND (p.prclose BETWEEN to_date(#{startPr}, 'yyyy-MM-dd') AND to_date(#{endPr}, 'yyyy-MM-dd'))
AND p.prstatus = #{complete}
AND c.gicode = #{gsCode}

