mapper.update:
UPDATE goodsstk
SET gsam = #{gsam}
WHERE gsmnum = #{gsmnum}

mapper.select:
SELECT d.dnum, s.cnnum, g.giname, r.csname, s.sham, s.shstatus, s.shdate, s.shnum, c.cnperiod FROM deliver d, contract c, goodsinfo g, costomer r, ship s WHERE (c.gicode = g.gicode AND c.cscode = r.cscode AND c.cnnum = s.cnnum AND s.shnum = d.shnum)
AND (s.shdate BETWEEN to_date(#{startSh}, 'yyyy-MM-dd') AND to_date(#{endSh}, 'yyyy-MM-dd'))
AND c.cnnum = #{cnNumber}

